        })
    }
        return (
            <form className="contact-form" onSubmit={handleSubmit}>
                <h1> Contact Form</h1>

               <label>Name</label>
               <input placeholder="Name"
               value={name}
               onChange = {(e) => setName(e.target.value)}
               />

               <label>Email</label>
