import { BrowserRouter, Route, Switch,  } from "react-router-dom";
import React, {useState, useEffect} from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "font-awesome/css/font-awesome.min.css";
import Lecture from "./components/lecture/lecture";
import Courses from "./components/courses /courses";
import Landing from "./components/landingpage/index";
import Login from "./components/login/login";
import Guru from "./components/guru/guru";
import Learning from "./components/learning/learning";
import Contact from "./components/contact/contact";
import Signup from "./components/signup/signup";
import { fire } from "./firebase/firebase.utils";

import "./App.scss";


const App = () => {
const [user,setUser] = useState('');
const [email,setEmail] = useState('');
const [password,setPassword]= useState('');
const [emailError, setEmailError] = useState('');
const [passwordError, setPasswordError]= useState('');
const [hasAccount, setHasAccount] = useState(false);

const clearInputs = () => {
  setEmail('')
  setPassword('');
}
const clearErrors = () =>{
  setEmailError('');
  setPasswordError('');
}

const handleLogin = () => {
  clearErrors();
  fire
  .auth()
  .signInWithEmailAndPassword(email, password)
  .catch(err => {
    switch(err.code){
      case "auth/invaild-email":
      case "auth/user-disabled":
        case "auth/user-not-found":
          setEmailError(err.message);
          break;
          case "auth/wrong-password":
            setPasswordError(err.message);
            break;
    }

  });
  const handleSignup = () => {
    clearErrors();
    fire
  .auth()
  .createUserWithEmailAndPassword(email, password)
  .catch(err => {
    switch(err.code){
      case "auth/email-already-in-use":
      case "auth/invalid-email":    
          setEmailError(err.message);
          break;
          case "auth/weak-password":
            setPasswordError(err.message);
            break;
    }

  }); 
};

const authListener = () => {
  fire.auth().onAuthStateChanged(user =>{
     if (user) {
       clearInputs();
       setUser(user);
     }else {
       setUser("");
     }
  });

};
useEffect(() => {
     authListener();
   },[]);
  };
    return (

      <BrowserRouter>

       {/* <Route path="/:page" component={NavBar } /> */}

        <div currentUser= {this.state.currentUser} >    
          <Switch>
            <Route path="/guru" exact component={Guru} />
            <Route path="/courses" exact component={Courses} />
            <Route path="/learning" exact component={Learning} />
            <Route path="/login" extact component={Login} />
            <Route path="/contact" exact component={Contact} />
            <Route path="/signup" exact component={Signup} />
            <Route path="/lecture" exact component={Lecture} />
            <Route component={Landing} />
          </Switch>
          <Login
          email ={email}
          setEmail={setEmail}
          password={password}
          setPassword={setPassword}
          handleLogin={handleLogin}
          handleSignup={handleSignup}
          hasAccount={hasAccount}
          setHasAccount={setHasAccount}
          emailError={emailError}
          passwordError={passwordError}  
          />

        </div>
      </BrowserRouter>

    );

  }


export default App;


