import React, {useState,  }  from 'react'
import "./login.css"
import { Link } from 'react-router-dom';
import {  signInWithGoogle } from '../../firebase/firebase.utils'
import { fire } from '../../firebase/firebase.utils';



const Login = () =>  {
const [user,setUser] = useState('');
const [email,setEmail] = useState('');
const [password,setPassword]= useState('');
const [emailError, setEmailError] = useState('');
const [passwordError, setPasswordError]= useState('');
const [hasAccount, setHasAccount] = useState(false);

const clearInputs = () => {
  setEmail('')
  setPassword('');
}
const clearErrors = () =>{
  setEmailError('');
  setPasswordError('');
}

const handleLogin = () => {
  clearErrors();
  fire
  .auth()
  .signInWithEmailAndPassword(email, password)
  .catch(err => {
    switch(err.code){
      case "auth/invaild-email":
      case "auth/user-disabled":
        case "auth/user-not-found":
          setEmailError(err.message);
          break;
          case "auth/wrong-password":
            setPasswordError(err.message);
            break;
    }

  });
  const handleSignup = () => {
    clearErrors();
    fire
  .auth()
  .createUserWithEmailAndPassword(email, password)
  .catch(err => {
    switch(err.code){
      case "auth/email-already-in-use":
      case "auth/invalid-email":    
          setEmailError(err.message);
          break;
          case "auth/weak-password":
            setPasswordError(err.message);
            break;
    }

  }); 
};

const authListener = () => {
  fire.auth().onAuthStateChanged(user =>{
     if (user) {
       clearInputs();
       setUser(user);
     }else {
       setUser("");
     }
  });



};


        return (


            <section className="login-container" onSubmit={this.handleSubmit}>
                <div className="loginContainer">
                    <label>Username</label>
                    <input 
                    type="text"
                    autoFocus required value={email}
                    onchange={e => setEmail(e.target.value)}
                     />
                     <p className="errorMsg">{emailError}</p>
                     <label > Password </label>
                     <input type="password" required value={password} onChange= {
                         e => setPassword(e.target.value)} />

                         <p className="errorMsg">{passwordError}</p>
                         <div className="btnContainer">
                             {hasAccount ? (
                                 <>
                                 <button onclick={handleLogin} >Sign in</button>
                                 <p> Don't have an account ?  <span onclick = { () => setHasAccount(!hasAccount) }> Sign up</span></p>
                                 </>

                             ) : (
                                <>
                                <button onclick={handleSignup}> Sign up</button>
                                <p> Have an account ?  <span onClick = {() => setHasAccount(!hasAccount)}> Sign in</span> </p>
                                </>

                             )}
                         </div>


            </div>

            <br />


            <button className="login-btn" type="submit" onChange={this.handleSubmit}>Log In</button> <br />
            <button className="login-btn" onClick={signInWithGoogle}>
                {''}
                Sign in with Google
                {''}
                </button>
            <div className="sign-up-link">
                <h6> Need an Account?</h6>
                <Link className="login-signup"  to="/signup">Signup now</Link>
            </div>

            </section>
        )
    }
}

export default Login
